import junit.framework.TestCase;import org.junit.Before;import org.junit.Test;import java.math.BigDecimal;import static junit.framework.TestCase.assertTrue;import static org.junit.Assert.assertEquals;public class CostCalculatorTest  {    CostCalculator costCalculator;    @Before    public void setUP(){        BigDecimal priceForOnePart = new BigDecimal(2);         costCalculator = new CostCalculator(priceForOnePart);    }    @Test    public void TestCostCalculatorPos(){        assertEquals(new BigDecimal(6),costCalculator.calculate(3));    }    @Test    public void TestCostCalculatorNull(){        assertEquals(new BigDecimal(0),costCalculator.calculate(0));    }    @Test    public void TestCostCalculatorNegativeValue(){        try{ costCalculator.calculate(-1);        }catch (IllegalArgumentException e){            assertTrue(true);        }    }}