import junit.framework.TestCase;import org.junit.Before;import org.junit.Test;import java.lang.reflect.Array;import java.util.Arrays;import static org.junit.Assert.assertEquals;import static org.junit.Assert.assertTrue;public class PaginatorTest  {    @Test    public void TestPosPaginatorShort(){        Paginator paginator = new Paginator(4);        String [] expectedArr = {"ABCD","F"};        String actual = "ABCDF";        String [] actualArr = paginator.paginate(actual);        assertTrue(Arrays.equals(actualArr,expectedArr));    }    @Test    public void TestPosPaginatorLong(){        Paginator paginator = new Paginator(5);        String [] expectedArr = {"ABCDF"};        String actual = "ABCDF";        String [] actualArr = paginator.paginate(actual);        assertTrue(Arrays.equals(expectedArr,actualArr));    }    @Test    public void TestPosPaginatorManyPart(){        Paginator paginator = new Paginator(1);        String [] expectedArr = {"A","B","C","D","F"};        String actual = "ABCDF";        String [] actualArr = paginator.paginate(actual);        assertTrue(Arrays.equals(expectedArr,actualArr));    }}